const ZIGZAG_ORDER: [usize; 64] = [
    0,  1,  5,  6, 14, 15, 27, 28,
    2,  4,  7, 13, 16, 26, 29, 42,
    3,  8, 12, 17, 25, 30, 41, 43,
    9, 11, 18, 24, 31, 40, 44, 53,
    10, 19, 23, 32, 39, 45, 52, 54,
    20, 22, 33, 38, 46, 51, 55, 60,
    21, 34, 37, 47, 50, 56, 59, 61,
    35, 36, 48, 49, 57, 58, 62, 63
];

pub fn zigzag_scan(block: &[[i32; 8]; 8]) -> [i32; 64] {
    let mut zigzag = [0i32; 64];
    for i in 0..8 {
        for j in 0..8 {
            let index = ZIGZAG_ORDER[i * 8 + j];
            zigzag[index] = block[i][j];
        }
    }
    zigzag
}

pub fn zigzag_unscan(zigzag: &[i32; 64]) -> [[i32; 8]; 8] {
    let mut block = [[0i32; 8]; 8];
    for i in 0..8 {
        for j in 0..8 {
            let index = ZIGZAG_ORDER[i * 8 + j];
            block[i][j] = zigzag[index];
        }
    }
    block
}

// u8: 0's number
// u8: for next step
// i16: value
// String: binary value for next step
pub fn run_length_encode(block: &[i32; 64]) -> Vec<(u8, u8, i16, String)> {
    let mut result = Vec::new();
    let mut count:u8 = 0;

    if block[0] > 1023 || block[0] < -1023 {
        panic!("Value out of range for 2047");
    }
    result.push((0, 0u8, block[0] as i16, String::new()));
    
    for i in 1..64 {
        
        // check if the current value is greater than the range of i16
        if block[i] > 1023 || block[i] < -1023 {
            panic!("Value out of range for 2047");
        }

        if block[i] == 0 {
            count += 1;
        } else {
            result.push((count, 0u8, block[i] as i16, String::new()));
            count = 0;
        }
    }
    result.push((0, 0u8, 0, String::new()));
    result
}


pub fn huffman_encode(zigzag_blocks: &mut[[i32; 64]], 
    dc_table: &[(&str, u8); 12], ac_table: &[(&str, (u8, u8)); 162]) 
    -> String {

    let mut ans = String::new();
    let mut prev_dc = 0;

    for block_idx in 0..zigzag_blocks.len() {
        let block = &mut zigzag_blocks[block_idx];
        // Differential coding
        let temp = block[0];
        block[0] -= prev_dc;
        if block[0] > 1023 || block[0] < -1023 {
            panic!("block 0 Value out of range for 2047");
        }
        prev_dc = temp;

        let mut encoded_block = run_length_encode(&block);

        for i in 0..encoded_block.len() {
            for j in 0..2047 {
                if TRANSFORM[j].0 == encoded_block[i].2{
                    encoded_block[i].1 = TRANSFORM[j].1;
                    encoded_block[i].3 = TRANSFORM[j].2.to_string();
                    break;
                }
            }
        }

        let dc = &encoded_block[0];
        for i in 0..dc_table.len() {
            if dc.1 == dc_table[i].1 {
                ans.push_str(dc_table[i].0);
                ans.push_str(dc.3.as_str());
                break;
            }
        }

        for i in 1..encoded_block.len() {
            let ac = &encoded_block[i];
            for j in 0..ac_table.len(){
                if (ac.0, ac.1) == ac_table[j].1 {
                    ans.push_str(ac_table[j].0);
                    ans.push_str(ac.3.as_str());
                    break;
                }
            }
        }
    }
    ans
}

// Huffman decode function
pub fn huffman_decode(encoded: &str, dc_table: &[(&str, u8)], 
ac_table: &[(&str, (u8, u8))]) -> Vec<[i32; 64]> {
    let mut blocks = Vec::new();
    let mut index = 0;
    let mut last_dc = 0;

    while index < encoded.len() {

        let mut zigzag_block = [0i32; 64];

        // Deal with DC coefficient
        let (dc_transform_length, dc_transform_value) = 
        decode_dc_table(&encoded[index..], dc_table);
        index += dc_transform_length;
        let diff_val = decode_tranform(&encoded[index..index + dc_transform_value as usize]) as i32;
        let true_val = diff_val + last_dc;
        last_dc = true_val;

        zigzag_block[0] = true_val;

        index += dc_transform_value as usize;

        // Deal with AC coefficients
        let mut count = 1;
        loop {
            let (ac_transform_length, ac_transform_value) = 
            decode_ac_table(&encoded[index..], ac_table);
            if ac_transform_value == (0, 0) {
                index += ac_transform_length;
                blocks.push(zigzag_block);
                break;
            }
            index += ac_transform_length;
            let transform_length = ac_transform_value.1 as usize;
            let transform_value = decode_tranform(&encoded[index..index + transform_length]) as i32;
            let zero_num = ac_transform_value.0 as usize;
            index += transform_length;
            count += zero_num;
            zigzag_block[count] = transform_value;
            count += 1;
        }
    }
    blocks
}

fn decode_dc_table(encoded: &str, table: &[(&str, u8)]) -> (usize, u8) {
    for &(code, value) in table {
        if encoded.starts_with(code) {
            return (code.len(), value);
        }
    }
    panic!("Invalid Huffman code");
}

fn decode_ac_table(encoded: &str, table: &[(&str, (u8, u8))]) -> (usize, (u8, u8)) {
    for &(code, value) in table {
        if encoded.starts_with(code) {
            return (code.len(), value);
        }
    }
    panic!("Invalid Huffman code");
}

fn decode_tranform(encoded: &str) -> i16 {
    for i in 0..2047 {
        if TRANSFORM[i].2 == encoded {
            return TRANSFORM[i].0;
        }
    }
    panic!("Invalid Huffman code");
}

